package edu.ung.phys.hist;

import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.util.Base64;

import javax.imageio.ImageIO;

import processing.core.PApplet;
import processing.core.PImage;

public class Base64Img2PImage extends PApplet {

	public static void main(String[] args) {
		PApplet.main("edu.ung.phys.hist.Base64Img2PImage");
	}
	
	
	public String imageString;
	PImage pimg;
	

	public void settings() {
		size(800, 600);
	}
	
	
	public void setup() {
		//imageString = "data:image/png;base64,";
		//imageString = "";
		imageString = "";

		
        BufferedImage bimage = null;
        byte[] imageByte;
        try {
        	imageByte = Base64.getDecoder().decode(imageString);
            ByteArrayInputStream bis = new ByteArrayInputStream(imageByte);
            bimage = ImageIO.read(bis);
            bis.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
        
		pimg = createImage(bimage.getWidth(), bimage.getHeight(), RGB);
		pimg.loadPixels();
		for(int x = 0; x < pimg.width; x++) {
			for(int y = 0; y < pimg.height; y++) {
				if(bimage.getRGB(x, y) != 0) pimg.set(x, y, bimage.getRGB(x, y));
				else pimg.set(x, y, color(200)); // to effectively make the background transparent
			}
		}
	}
	
	
	public void draw() {
		background(200);
		image(pimg, 50, 50);
	}

}
